import { useState } from "react";

const EditForm = ({ userTarget, index, setEdit }) => {
  const [user, setUserState] = useState({
    name: userTarget.name,
    tel: userTarget.tel,
    email: userTarget.email,
    membership: userTarget.membership,
    date: userTarget.date,
  });

  let editUserHandler = (e) => {
    e.preventDefault();
    userTarget.dispatch({
      type: "edit_user",
      payload: { index, user },
    });

    setEdit(false);
  };
  return (
    <tr className="bg-white border-b hover:bg-gray-50 dark:hover:bg-gray-600">
      <td className="px-6 py-4  text-right">
        <input
          id="name"
          type="text"
          defaultValue={user.name}
          className="w-full border-cyan-600 border-2 text-center focus:text-cyan-600 focus:outline-none focus:border-gray-500 rounded"
          onChange={(e) =>
            setUserState({
              ...user,
              name: e.target.value,
            })
          }
        />
      </td>
      <td className="px-6 py-4  text-right">
        <input
          id="tel"
          type="number"
          defaultValue={user.tel}
          className="w-full border-cyan-600 border-2 text-center focus:text-cyan-600 focus:outline-none focus:border-gray-500 rounded"
          onChange={(e) =>
            setUserState({
              ...user,
              tel: e.target.value,
            })
          }
        />
      </td>
      <td className="px-6 py-4  text-right">
        <input
          id="email"
          type="email"
          defaultValue={user.email}
          className="w-full border-cyan-600 border-2 text-center focus:text-cyan-600 focus:outline-none focus:border-gray-500 rounded"
          onChange={(e) =>
            setUserState({
              ...user,
              email: e.target.value,
            })
          }
        />
      </td>
      <td className="px-6 py-4  text-right">
        <td className="px-4 py-2 whitespace-nowrap text-center text-gray-700">
          <select
            className="input text-xs"
            value={user.type}
            onChange={handleInputes}
            name="type"
          >
            <option value="normal">کاربر معمولی</option>
            <option value="admin">کاربر ادمین</option>
          </select>
        </td>
      </td>
      <td className="px-6 py-4  text-right">
        <input
          id="date"
          type="Date"
          defaultValue={user.date}
          className="w-full border-cyan-600 border-2 text-center focus:text-cyan-600 focus:outline-none focus:border-gray-500 rounded"
          onChange={(e) =>
            setUserState({
              ...user,
              date: e.target.value,
            })
          }
        />
      </td>
      <td className="px-6 py-4 flex justify-evenly">
        <span
          className="bg-cyan-500 hover:bg-cyan-500 transition duration-200 cursor-pointer text-white px-2 py-1 rounded-md"
          onClick={(e) => editUserHandler(e)}
        >
          Apply
        </span>
      </td>
    </tr>
  );
};
export default EditForm;
