const DeleteUser = ({ setUsers, id, user }) => {
     setUsers((prevState) => {
       return {
         user: prevState.user.filter((item) => item.key !== id),
       };
     });
  const handleDelete = () => {
    user = user.filter((user) => user.id !== id);
    setUsers(user);
  };

  return (
    <button className="btn-cancel" onClick={() => handleDelete()}>
      حذف
    </button>
  );
};
export default DeleteUser;
