import moment from 'jalali-moment'

///import { sweetalert } from "../../helpers/helpers";

const TableItem = ({index,user}) => {
      let deleteHandler = () => {
        user.dispatch({ type: "delete_user", payload: index });

      };
    return (
        <tr className="bg-white border-b hover:bg-gray-50 dark:hover:bg-gray-600">
            <td className="px-6 py-4  text-right">{user.userName}</td>
            <td className="px-6 py-4  text-right">{user.tel}</td>
            <td className="px-6 py-4  text-right">{user.email}</td>
            <td className="px-6 py-4  text-right">{user.membership}</td>
            <td className="px-6 py-4  text-right">{moment(new Date(user.date), 'YYYY/MM/DD').locale('fa').format('YYYY/MM/DD')}</td>
            <td className="px-6 py-4 flex justify-evenly">
                <button onClick={deleteHandler} className=" mr-4 font-medium text-red-600 hover:underline text-right">
                    حذف
                </button>
                <button className=" mr-4 font-medium text-indigo-700 hover:underline text-right">
                    ویرایش
                </button>
            </td>
        </tr>
    )
}

export default TableItem
