import { useState } from "react";

function EditUserForm({ target, setEdit, setUsers }) {
    const [user, setUserState] = useState({
      name: target.name,
      family: userTarget.family,
      nickname: userTarget.nickname,
      email: userTarget.email,
      gender: userTarget.gender,
      isAdmin: userTarget.isAdmin,
      isActive: userTarget.isActive,
      createdAt: userTarget.createdAt,
    });

  console.log(user);
  const handleInputs = (event) => {
    let name = event.target.name;
    let value = event.target.value;

    setUsers({
      ...user,
      [name]: value,
    });
  };

  const handleSubmit = () => {
    setUsers((prevState) =>
      prevState.map((u) => (u.key === user.key ? user : u))
    );
    setEdit(false);
  };

  return (
    <tr className="hover:bg-gray-100">
      <td className="px-4 py-2 whitespace-nowrap text-center text-gray-700">
        <input
          required
          id="name"
          type="text"
          value={user.userName}
          className="input text-xs"
          onChange={handleInputs}
          name="name"
        />
      </td>

      <td className="px-4 py-2 whitespace-nowrap text-center text-gray-700">
        <input
          type="number"
          value={user.tel}
          className="input text-xs"
          onChange={handleInputs}
          name="tel"
        />
      </td>
      <td className="px-4 py-2 whitespace-nowrap text-center text-gray-700">
        <input
          type="text"
          value={user.email}
          className="input text-xs"
          onChange={handleInputs}
          name="email"
        />
      </td>

      <td className="px-4 py-2 whitespace-nowrap text-center text-gray-700">
        <select
          className="input text-xs"
          value={user.membership}
          onChange={handleInputs}
          name="membership"
        >
          <option value="کاربر">کاربر</option>
          <option value="ادمین">ادمین</option>
        </select>
      </td>
      <td
        className="px-4 py-2 whitespace-nowrap text-center text-gray-700"
        dir="ltr"
      >
        <p className="text-xs">{user.date}</p>
      </td>
      <td className="px-4 py-2 whitespace-nowrap space-x-2 space-x-reverse">
        <button className="btn-success btn-sm" onClick={handleSubmit}>
          بروزرسانی
        </button>
        <button className="btn-cancel btn-sm" onClick={() => setEdit(false)}>
          انصراف
        </button>
      </td>
    </tr>
  );
}

export default EditUserForm;
