const DeleteUser = ({ setUsers, id, user }) => {
   
  const handleDelete = () => {
    setUsers((prevState) => {
      return {
        user: prevState.user.filter((item) => item.id !== id),
      };
    });
  };

  return (
    <button className="btn-cancel" onClick={() => handleDelete()}>
      حذف
    </button>
  );
};
export default DeleteUser;
